mapper.insert:
INSERT INTO t_feed
SET iuser = #{iuser}
, contents = #{contents}
, location = #{location}

mapper.select:
SELECT A.ifeed, A.contents, A.location, A.created_at AS createdAt , B.iuser AS writerIuser, B.nm AS writerNm, B.pic AS writerPic , if(C.iuser is null, 0, 1) AS isFav FROM t_user B JOIN t_feed A ON A.iuser = B.iuser
INNER JOIN t_feed_fav C
LEFT JOIN t_feed_fav C
ON A.ifeed = C.ifeed AND C.iuser = #{loginedIuser}
WHERE A.iuser = #{targetIuser}
ORDER BY ifeed DESC limit #{startIdx}, #{rowCount}

SELECT iuser
FROM t_feed
WHERE ifeed = #{ifeed}

mapper.delete:
DELETE FROM t_feed
WHERE ifeed = #{ifeed}
AND iuser = #{iuser}
